import io
import json

from pycoin.serialize import h2b
from pycoin.tx import Tx
from pycoin.tx.pay_to import build_p2sh_lookup
from pycoin.tx.tx_utils import LazySecretExponentDB

__author__ = 'devrandom'

import unittest
from multisigcore import Oracle, local_sign
from pycoin.key.BIP32Node import BIP32Node

TEST_PATH = "0/0/1"

class OracleTest(unittest.TestCase):
    def setUp(self):
        self.wallet_private_key = BIP32Node.from_master_secret("aaa-2015-02-10".encode('utf8'))
        self.recover_key = BIP32Node.from_hwif("xpub661MyMwAqRbcGmRK6wKJrfMXoenZ86PMUfBWNvmmp5c51PyyzjY7yJL9venRUYqmSqNo7iGqHbVWkTVYzY2drw57vr45iHxV7NsAqF4ZWg5")
        self.wallet_key = BIP32Node.from_hwif("xpub661MyMwAqRbcFqtR38s6kVQudQxKpHzNJyWEXmz2TnuDoR8FpZR7EuL158B5QDaYvxCfp3LAEa8VwdtxNgKHNha4JKqGrqkzBGboJFwgyrR")
        self.oracle_key = BIP32Node.from_hwif("xpub68rQ8y4gfKeqG3sxQQE7uNwjnjcTiEZDQCrr2witfS3VrZ3QkeR2XuiQWUpdQRUVShcyVzjX2ZvDWHS2SZcZJXaGC7HybSPVMDXErbRRHwn")
        self.tx_db = dict()
        self.input_tx = Tx.tx_from_hex("")
        self.tx_db[self.input_tx.hash()] = self.input_tx
        self.oracle = Oracle(keys=[self.wallet_key, self.recover_key], tx_db=self.tx_db)
        self.oracle.oracle_keys = [self.oracle_key]

    def test_payto(self):
        payto = self.oracle.payto(TEST_PATH)
        self.assertEqual("34DjTcNWGReJV4xx7R1AWK7FTz3xMwMcjA", payto.address())

    def test_sign(self):
        # generated with `tx -i 34DjTcNWGReJV4xx7R1AWK7FTz3xMwMcjA  3Ph5UGYHCyvYFQifw76T8iqKL9EkGKDBMz/100000 -o tx.hex`
        f = io.BytesIO(h2b("01000000019cb9e92cd3f91087852382150f19b5d99259be47106d860055d1afb8110022250000000000ffffffff01d06c04000000000017a914f155ba65bdb30930da320ec51a0d6c913dfce06b8700000000e09304000000000017a9141bbf6712630dd01fab4e70ac91a06925d138f27387"))

        unsigned = Tx.parse(f)
        unsigned.parse_unspents(f)
        script = self.oracle.script(TEST_PATH)
        child_key = self.wallet_private_key.subkey_for_path(TEST_PATH)
        local_sign(unsigned, [script], [child_key])
        req = self.oracle.create_oracle_request([TEST_PATH], [], None, unsigned)
        self.assertEqual("",
                         req['transaction']['bytes'])

    def test_sign_change(self):
        # generated with `tx -i 34DjTcNWGReJV4xx7R1AWK7FTz3xMwMcjA  3Ph5UGYHCyvYFQifw76T8iqKL9EkGKDBMz/90000 34DjTcNWGReJV4xx7R1AWK7FTz3xMwMcjA/200000 -o tx1.hex`
        # signing would be with `digital_oracle sign P:aaa-2015-02-10 P:bbb tx1.hex -i 0/0/1 -c - 0/0/1`
        f = io.BytesIO(h2b("01000000019cb9e92cd3f91087852382150f19b5d99259be47106d860055d1afb8110022250000000000ffffffff02905f01000000000017a914f155ba65bdb30930da320ec51a0d6c913dfce06b87400d03000000000017a9141bbf6712630dd01fab4e70ac91a06925d138f2738700000000e09304000000000017a9141bbf6712630dd01fab4e70ac91a06925d138f27387"))
        unsigned = Tx.parse(f)
        unsigned.parse_unspents(f)
        script = self.oracle.script(TEST_PATH)
        child_key = self.wallet_private_key.subkey_for_path(TEST_PATH)
        local_sign(unsigned, [script], [child_key])
        req = self.oracle.create_oracle_request([TEST_PATH], [None, TEST_PATH], None, unsigned)
        self.assertEqual("",
                         req['transaction']['bytes'])
        JSON = '{"walletAgent": "digitaloracle-pycoin-0.01", "transaction": {"outputChainPaths": [null, "0/0/1"], "bytes": "", "inputScripts": ["522102fa0e06db47e8924274c670503238db30367d11ccaca00d385ac370fed93578d2210379014532a465b19fcf1ead9921488274821fd58178542b2aa54007bcc5a29d34210381c235ee18d9e85e3b28200200df3a2276c6b9473f18946ef8740ccaebfa4b1e53ae"], "masterKeys": ["xpub661MyMwAqRbcFqtR38s6kVQudQxKpHzNJyWEXmz2TnuDoR8FpZR7EuL158B5QDaYvxCfp3LAEa8VwdtxNgKHNha4JKqGrqkzBGboJFwgyrR", "xpub661MyMwAqRbcGmRK6wKJrfMXoenZ86PMUfBWNvmmp5c51PyyzjY7yJL9venRUYqmSqNo7iGqHbVWkTVYzY2drw57vr45iHxV7NsAqF4ZWg5"], "chainPaths": ["0/0/1"], "inputTransactions": [""]}}'
        self.assertEqual(req, json.loads(JSON))